func splitArray(nums []int) bool {
	n := len(nums)
	presum := make([]int, n+1)
	for i := range nums {
		presum[i+1] = presum[i] + nums[i]
	}

	sumRanges := make(map[int]map[int][]int)
	for i := range nums {
		for j := i + 1; j <= n; j++ {
			s := presum[j] - presum[i]
			if _, exists := sumRanges[s]; !exists {
				sumRanges[s] = make(map[int][]int)
			}
			sumRanges[s][i] = append(sumRanges[s][i], j-1)
		}
	}

	for _, startEnd := range sumRanges {
		seen := make(map[[2]int]struct{})
		if len(startEnd) < 4 {
			continue
		}
		if dfs(seen, startEnd, 0, 0, n) {
			return true
		}
	}
	return false
}

func dfs(seen map[[2]int]struct{}, indices map[int][]int, count, pos, n int) bool {
	if _, exists := seen[[2]int{count, pos}]; exists {
		return false
	}
	if count > 4 || pos > n+1 {
		seen[[2]int{count, pos}] = struct{}{}
		return false
	}
	if count == 4 && pos == n+1 {
		return true
	}
	for _, end := range indices[pos] {
		if dfs(seen, indices, count+1, end+2, n) {
			return true
		}
	}
	seen[[2]int{count, pos}] = struct{}{}
	return false
}
