// uint128 represents a uint128 using two uint64s.
type uint128 struct {
	hi uint64
	lo uint64
}

// and returns the bitwise AND of u and m (u&m).
func (u uint128) and(m uint128) uint128 {
	return uint128{u.hi & m.hi, u.lo & m.lo}
}

// or returns the bitwise OR of u and m (u|m).
func (u uint128) or(m uint128) uint128 {
	return uint128{u.hi | m.hi, u.lo | m.lo}
}

// not returns the bitwise NOT of u.
func (u uint128) not() uint128 {
	return uint128{^u.hi, ^u.lo}
}

// shiftLeft shifts u left by b
func (u uint128) shiftLeft(b uint64) uint128 {
	return uint128{
		(u.hi << b) | (u.lo >> (64 - b)) | (u.lo << (b - 64)),
		u.lo << b,
	}
}

// shiftLeft shifts u right by b
func (u uint128) shiftRight(b uint64) uint128 {
	return uint128{
		u.hi >> b,
		(u.lo >> b) | (u.hi << (64 - b)) | (u.hi >> (b - 64)),
	}
}

// bit returns the bit at index b
func (u uint128) bit(b uint64) uint8 {
	return uint8(u.shiftRight(b).lo & 1)
}

func (u uint128) String() string {
	f := "%0" + fmt.Sprint(64) + "b"
	s := []byte(fmt.Sprintf(f+f, u.hi, u.lo))
	for l, r := 0, len(s)-1; l < r; l, r = l+1, r-1 {
		s[l], s[r] = s[r], s[l]
	}
	return string(s)
}

func findBall(grid [][]int) []int {
	// Time for some overengineering again..
	n := len(grid[0])
	pos := make([]int, n)
	for i := range pos {
		pos[i] = i
	}
	rows := make([]uint128, len(grid))
	for i := range grid {
		for j := n - 1; j >= 0; j-- {
			rows[i] = rows[i].shiftLeft(1)
			rows[i].lo |= uint64(1+grid[i][j]) / 2
		}
		rows[i] = rows[i].shiftLeft(1)
		rows[i].lo |= 1 // first position is going right
	}

	for _, row := range rows {
		validRight := row.and(row.shiftRight(1))
		validLeft := row.not().and(row.not().shiftLeft(1))
		valid := validLeft.or(validRight)
		for j := range pos {
			pos[j] = pos[j] -
				int(validLeft.bit(uint64(pos[j]+1))) +
				int(validRight.bit(uint64(pos[j]+1))) -
				int(valid.not().bit(uint64(pos[j]+1)))<<7
		}
	}
	for i := range pos {
		if pos[i] < 0 {
			pos[i] = -1
		}
	}
	return pos
}
